mapper.select:
SELECT RN , BOARD_NUM , BOARD_TITLE , MEMBER_ID , BOARD_REG_DATE , BOARD_READ_CNT , IS_SECRET , LIKE_COUNT , HATE_COUNT FROM ( SELECT ROWNUM AS RN , BOARD_NUM , BOARD_TITLE , MEMBER_ID , BOARD_REG_DATE , BOARD_READ_CNT , IS_SECRET , LIKE_COUNT , HATE_COUNT FROM ( SELECT BOARD_NUM , BOARD_TITLE , MEMBER_ID , TO_CHAR(BOARD_REG_DATE, 'YYYY-MM-DD') AS BOARD_REG_DATE , BOARD_READ_CNT , IS_SECRET , (SELECT COUNT(BOARD_LIKE_NUM) FROM BOARD_LIKE WHERE BOARD_NUM = BOARD.BOARD_NUM) AS LIKE_COUNT , (SELECT COUNT(BOARD_HATE_NUM) FROM BOARD_HATE WHERE BOARD_NUM = BOARD.BOARD_NUM) AS HATE_COUNT FROM BOARD WHERE
UPPER(MEMBER_ID) LIKE UPPER('%'||#{memberId}||'%')
AND UPPER(BOARD_TITLE) LIKE UPPER('%'||#{boardTitle}||'%')
AND UPPER(BOARD_CONTENT) LIKE UPPER('%'||#{boardContent}||'%')
AND ( UPPER(BOARD_TITLE) LIKE UPPER('%'||#{boardTitle}||'%') OR UPPER(BOARD_CONTENT) LIKE UPPER('%'||#{boardContent}||'%') )
AND TO_CHAR(BOARD_REG_DATE, 'YYYY-MM-DD') >= #{fromDate}
AND TO_CHAR(BOARD_REG_DATE, 'YYYY-MM-DD') <= #{toDate}
ORDER BY ${orderBy} DESC ) )
WHERE RN >= #{startNum} AND RN <= #{endNum}

mapper.insert:
INSERT INTO BOARD (
			BOARD_NUM
			, BOARD_TITLE
			, BOARD_CONTENT
			, MEMBER_ID
			, IS_SECRET

	) VALUES( 
			(SELECT NVL(MAX(BOARD_NUM),0)+1 FROM community_BOARD)
			, #{boardTitle}
			, #{boardContent}
			, #{memberId}
			, #{isSecret}
)

mapper.update:
UPDATE BOARD
SET BOARD_READ_CNT = BOARD_READ_CNT + 1
WHERE BOARD_NUM = #{boardNum}

UPDATE BOARD 
SET BOARD_CONTENT = #{boardContent}
WHERE BOARD_NUM = #{boardNum}
